    # Initialize the Union Find Data Structure
    root = list(range(m * n))
    size = [1] * (m * n)

    def find(x):
        if x!= root[x]:
            root[x] = find(root[x])
        return root[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x!= root_y:
            root[root_x] = root_y
            size[root_y] += size[root_x]

    # Process Each Add Land Operation
    answer = []
