        ri, ci = operation
        i = ri * n + ci
        if ri > 0:
            union(i, (ri - 1) * n + ci)
        if ri < m - 1:
            union(i, (ri + 1) * n + ci)
        if ci > 0:
            union(i, ri * n + ci - 1)
        if ci < n - 1:
            union(i, ri * n + ci + 1)
