def generate_palindromes(s):
    n = len(s)
    count = Counter(s)

    # Check if palindrome is possible
    odd_count = 0
    for char, cnt in count.items():
        if cnt % 2!= 0:
            odd_count += 1
    if (n % 2 == 0 and odd_count!= 0) or (n % 2!= 0 and odd_count!= 1):
        return []

